import React, { Fragment, useRef, useEffect, createContext } from "react";
import { Modal, Drawer, Space, Button, Divider, Row, Col, Select, Form } from "antd";
import { connect } from "react-redux";
import { MonacoEditor } from "~components";
import { useSet } from "~common/hooks";
import { echartBarAPI } from "@/api";

const { Option } = Select;
const Compose = createContext();
const layout = {
  labelCol: {
    span: 24
  },
  wrapperCol: {
    span: 24
  }
};

/**
 * æ•°æ®ç®¡ç†æ•°æ®
 * @param {value} æ•°æ®
 * @param {onChange} é€šè¿‡åˆ·æ–°æ•°æ®æ”¹å˜
 */
const DataSource = ({ name, value, options, onChange, api, sql }) => {
  const codeRef = useRef();
  const [form] = Form.useForm();
  const [state, dispath] = useSet({
    typeOf: "json",
    visible: false,
    visible1: false,
    disabled: true,
    code: { data: null },
    apiValue: null,
    apiList: api,
    sqlValue: null,
    sqlList: sql
  });

  useEffect(() => {
    dispath({
      typeOf: value.dataType,
      code: value.data
    });
  }, []);

  const handleOk = () => {
    echartBarAPI({ code: "// try to write code somewhere ğŸ˜ˆ" }).then((res) => {
      dispath({
        disabled: false,
        code: res.data
      });
    });
  };

  const handleChange = (value) => {
    dispath({
      typeOf: value
    });
  };

  const onChangeApi = (value) => {
    const result = state.apiList.find((item) => item.id === value).data;
    dispath({
      apiValue: value,
      code: result
    });
  };

  const onChangeSql = (value) => {
    const result = state.sqlList.find((item) => item.id === value).data;
    dispath({
      sqlValue: value,
      code: result
    });
  };

  return (
    <Compose.Provider value={{ state, dispath }}>
      <Form {...layout} form={form} style={{ marginTop: 5 }}>
        <Form.Item label="æ•°æ®æºç±»å‹">
          <Select value={state.typeOf} onChange={handleChange}>
            <Option value="json">JSONæ•°æ®</Option>
            <Option value="api">APIæ¥å£</Option>
            <Option value="sql">MySQLæ•°æ®åº“</Option>
          </Select>
        </Form.Item>
        {state.typeOf === "json" && (
          <Fragment>
            <MonacoEditor ref={codeRef} height={options.height} language="json" value={value.data} />
            <Space size="small" style={{ marginTop: 10 }}>
              <Button
                type="primary"
                onClick={() => {
                  dispath({
                    visible1: true,
                    code: codeRef.current.getValue()
                  });
                }}
              >
                è°ƒè¯•
              </Button>
              <Button
                type="primary"
                onClick={() => {
                  let _value = Object.assign(value, {
                    dataType: state.typeOf,
                    data: codeRef.current.getValue()
                  });
                  onChange(name, _value);
                }}
              >
                åˆ·æ–°æ•°æ®
              </Button>
            </Space>
          </Fragment>
        )}

        {state.typeOf === "api" && (
          <Form.Item label="æ•°æ®æ¨¡å‹">
            <Select
              placeholder="è¯·é€‰æ‹©æ•°æ®æ¨¡å‹"
              defaultValue={state.apiValue}
              showSearch
              filterOption={(input, option) => option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0}
              onChange={onChangeApi}
            >
              {state.apiList.map((item) => (
                <Option key={item.id} value={item.id}>
                  {item.name}
                </Option>
              ))}
            </Select>
            <Space size="small" style={{ marginTop: 15 }}>
              <Button
                type="primary"
                onClick={() => {
                  dispath({
                    visible1: true
                  });
                }}
              >
                è°ƒè¯•
              </Button>
              <Button
                type="primary"
                onClick={() => {
                  let _value = Object.assign(value, {
                    dataType: state.typeOf,
                    data: state.code
                  });
                  onChange(name, _value);
                }}
              >
                åˆ·æ–°æ•°æ®
              </Button>
            </Space>
          </Form.Item>
        )}

        {state.typeOf === "sql" && (
          <Form.Item label="æ•°æ®æ¨¡å‹">
            <Select
              placeholder="è¯·é€‰æ‹©æ•°æ®æ¨¡å‹"
              defaultValue={state.sqlValue}
              showSearch
              filterOption={(input, option) => option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0}
              onChange={onChangeSql}
            >
              {state.sqlList.map((item) => (
                <Option key={item.id} value={item.id}>
                  {item.name}
                </Option>
              ))}
            </Select>

            <Space size="small" style={{ marginTop: 15 }}>
              <Button
                type="primary"
                onClick={() => {
                  dispath({
                    visible: true,
                    disabled: true
                  });
                }}
              >
                æŸ¥è¯¢
              </Button>
              <Button
                type="primary"
                onClick={() => {
                  dispath({
                    visible1: true
                  });
                }}
              >
                è°ƒè¯•
              </Button>
            </Space>
          </Form.Item>
        )}
      </Form>
      {/* æŸ¥è¯¢ */}
      <Modal
        visible={state.visible}
        width={1080}
        destroyOnClose={true}
        closable={false}
        title="è¯·æ±‚æ•°æ®"
        footer={[
          <Button key="request" type="dashed" onClick={handleOk}>
            è°ƒè¯•
          </Button>,
          <Button
            key="cancel"
            onClick={() => {
              dispath({
                visible: false,
                code: { data: null }
              });
            }}
          >
            å–æ¶ˆ
          </Button>,
          <Button
            key="ok"
            type="primary"
            disabled={state.disabled}
            onClick={() => {
              dispath({
                visible: false,
                code: { data: null }
              });

              onChange(state.code);
            }}
          >
            æ›´æ–°
          </Button>
        ]}
      >
        <Row gutter={[16, 16]}>
          <Col span={12}>
            <Divider orientation="left" plain>
              æŸ¥è¯¢è¡¨è¾¾å¼
            </Divider>
            <MonacoEditor height={300} language="mysql" value="// try to write code somewhere ğŸ˜ˆ" />
          </Col>
          <Col span={12}>
            <Divider orientation="left" plain>
              æ ¼å¼åŒ–åç»“æœ
            </Divider>
            <MonacoEditor height={300} value={state.code} readOnly={true} />
          </Col>
        </Row>
      </Modal>
      {/* è°ƒè¯• */}
      <Drawer
        visible={state.visible1}
        title="æ•°æ®å“åº”ç»“æœ"
        width={400}
        placement="right"
        onClose={() => {
          dispath({ visible1: false });
        }}
      >
        <MonacoEditor height={800} value={state.code} readOnly={true} />
      </Drawer>
    </Compose.Provider>
  );
};

export default connect((state) => ({
  api: state.component.api,
  sql: state.component.sql
}))(DataSource);
